    addScrollReveal = () => {
        scrollreveal({
            mobile: false
        }).reveal('.reveal-h1', { 
            distance: '50px',
            // // reset: true,
            // duration: 1000,
            useDelay: "onload",
            delay: 500,
            easing: 'ease-in-out',
            viewFactor: 0.33,
            // reset: true,
            // init: true
        });
    }

    detectMetaMask = () => {
        if (typeof window.ethereum !== 'undefined') {
            this.setState({
                hasMetamask: window.ethereum.isMetaMask
            })
        }
    }

    subscribe = (text) => {
        axios.post('https://api.darwinia.network/api/subscribe?email=' + text)
            .then((response) => {
                if (response.data.code === 0) {
                    this.setState({
                        info: this.props.t('home_page:subscribe_tip')
                    })
                }
            })
            .catch((error) => {
                // console.log(error);
            });
    }

    changeTextValue = (k, e) => {
        this.setState({
            [k]: e.target.value
        })
    }

    renderPartner = () => {
        return this.partners.map((partner, index) => {
            return (
                <Col key={index} xs={6} md={4} lg={2}>
                    <a target="_blank" rel="noopener noreferrer" href={this.partnerInfo[partner].url}>
                        <img alt={this.partnerInfo[partner].name} src={this.partnerInfo[partner].logo} />
                        <h3>{this.partnerInfo[partner].name}</h3>
                    </a>
                </Col>
            )
        })
    }

    addToken = async (type) => {
        const token = this.tokenInfo[type];
        const provider = window.web3.currentProvider;
        provider.sendAsync({
            method: 'metamask_watchAsset',
            params: {
                "type": "ERC20",
                "options": {
                    "address": token.tokenAddress,
                    "symbol": token.tokenSymbol,
                    "decimals": token.tokenDecimals,
                    "image": token.tokenImage,
                },
            },
            id: Math.round(Math.random() * 100000),
        }, (err, added) => {
            if (err || 'error' in added) {
                console.log(err)
                return
            }
        })
    }

